.weather .details .forcast h4,
.weather .details .forcast p {
  display: block;
  width: 50px;
  text-align: center;
  color: #444;
}
.weather .details .forcast p:last-child {
  color: #aaa;
}
.weather .details .forcast i {
  margin: 10px 0;
}

.weather {
  background-color: white;
  transition: background-color 2s ease;
}

